mapper.select:
select a.ID,a.MNAME, a.UPPDER_ID, a.NLEVEL, a.SYSTEM_ID, a.NINDEX, a.BC_FLAG, a.PAGE, a.PIC, a.NAMEPY, a.NAMEWB, a.REMARK, a.UPD_USER, a.UPD_TIME, a.CRT_USER, a.CRT_TIME, ifnull(b.MNAME,'父级') FATHER_MNAME from sys_menu a LEFT JOIN sys_menu b on a.UPPDER_ID=b.ID
and (namepy like concat(concat('%',#{namepy,jdbcType=VARCHAR}),'%') or namewb like concat(concat('%',#{namepy,jdbcType=VARCHAR}),'%'))
and a.UPPDER_ID=#{uppderId,jdbcType=VARCHAR}
order by CAST(a.NINDEX AS DECIMAL) asc;

select a.ID,a.MNAME, a.UPPDER_ID, a.NLEVEL, a.SYSTEM_ID, a.NINDEX, a.BC_FLAG, a.PAGE, a.PIC, a.NAMEPY, a.NAMEWB, a.REMARK, a.UPD_USER, a.UPD_TIME, a.CRT_USER, a.CRT_TIME, ifnull(b.MNAME,'父级') FATHER_MNAME from sys_menu a LEFT JOIN sys_menu b on a.UPPDER_ID=b.ID WHERE a.UPPDER_ID !='0'
and (a.namepy like concat(concat('%',#{namepy,jdbcType=VARCHAR}),'%') or a.namewb like concat(concat('%',#{namepy,jdbcType=VARCHAR}),'%'))
and a.UPPDER_ID=#{uppderId,jdbcType=VARCHAR}
AND a.ID IN ( SELECT DISTINCT srm.MENU_ID MENU_ID FROM( SELECT sor.ROLE_ID FROM sys_oper_role sor WHERE sor.LOGINCODE = #{logincode,jdbcType=VARCHAR}) rli LEFT JOIN ( SELECT srmm.MENU_ID,srmm.LOGINCODE FROM sys_right_modset srmm WHERE srmm.OBJFLAG = 2 ) srm ON rli.ROLE_ID = srm.LOGINCODE WHERE MENU_ID IS NOT NULL )
order by CAST(a.NINDEX AS DECIMAL) asc;
